function [ y, gy, hy ] = extRosenbrock8( x )
t = num2cell(x);
[v1, v2, v3, v4, v5, v6, v7, v8] = t{:};

y = (conj(v1) - 1)*(v1 - 1) + (conj(v3) - 1)*(v3 - 1)...
    + (conj(v5) - 1)*(v5 - 1) + (conj(v7) - 1)*(v7 - 1)...
    + (10*conj(v2) - 10*conj(v1)^2)*(- 10*v1^2 + 10*v2)...
    + (10*conj(v4) - 10*conj(v3)^2)*(- 10*v3^2 + 10*v4)...
    + (10*conj(v6) - 10*conj(v5)^2)*(- 10*v5^2 + 10*v6)...
    + (10*conj(v8) - 10*conj(v7)^2)*(- 10*v7^2 + 10*v8);

gy = [v1 + conj(v1) - 20*v1*(10*conj(v2) - 10*conj(v1)^2)...
    - 20*conj(v1)*(- 10*v1^2 + 10*v2) - 2;
    100*v2 + 100*conj(v2) - 100*conj(v1)^2 - 100*v1^2;
    v3 + conj(v3) - 20*v3*(10*conj(v4) - 10*conj(v3)^2)...
    - 20*conj(v3)*(- 10*v3^2 + 10*v4) - 2;
    100*v4 + 100*conj(v4) - 100*conj(v3)^2 - 100*v3^2;
    v5 + conj(v5) - 20*v5*(10*conj(v6) - 10*conj(v5)^2)...
    - 20*conj(v5)*(- 10*v5^2 + 10*v6) - 2;
    100*v6 + 100*conj(v6) - 100*conj(v5)^2 - 100*v5^2;
    v7 + conj(v7) - 20*v7*(10*conj(v8) - 10*conj(v7)^2)...
    - 20*conj(v7)*(- 10*v7^2 + 10*v8) - 2;
    100*v8 + 100*conj(v8) - 100*conj(v7)^2 - 100*v7^2];

hy = [200*conj(v1)^2 - 200*conj(v2) - 200*v2 + 800*v1*conj(v1) + 200*v1^2 + 2, - 200*v1 - 200*conj(v1),                                                                       0,                       0,                                                                       0,                       0,                                                                       0,                       0;
    - 200*v1 - 200*conj(v1),                     200,                                                                       0,                       0,                                                                       0,                       0,                                                                       0,                       0;
    0,                       0, 200*conj(v3)^2 - 200*conj(v4) - 200*v4 + 800*v3*conj(v3) + 200*v3^2 + 2, - 200*v3 - 200*conj(v3),                                                                       0,                       0,                                                                       0,                       0;
    0,                       0,                                                 - 200*v3 - 200*conj(v3),                     200,                                                                       0,                       0,                                                                       0,                       0;
    0,                       0,                                                                       0,                       0, 200*conj(v5)^2 - 200*conj(v6) - 200*v6 + 800*v5*conj(v5) + 200*v5^2 + 2, - 200*v5 - 200*conj(v5),                                                                       0,                       0;
    0,                       0,                                                                       0,                       0,                                                 - 200*v5 - 200*conj(v5),                     200,                                                                       0,                       0;
    0,                       0,                                                                       0,                       0,                                                                       0,                       0, 200*conj(v7)^2 - 200*conj(v8) - 200*v8 + 800*v7*conj(v7) + 200*v7^2 + 2, - 200*v7 - 200*conj(v7);
    0,                       0,                                                                       0,                       0,                                                                       0,                       0,                                                 - 200*v7 - 200*conj(v7),                     200
    ];

end

